from pymongo import Connectionimport codecscon = Connection()db = con.wooyun_2bug_type_translation = db.bug_type_translationf_bug_type_translation = codecs.open('../data/bug_type_translation.csv','r', 'utf-8')for item in f_bug_type_translation:	Chinese = item.split(',')[0].replace('\"','').strip()	English = item.split(',')[1].replace('\"','').strip()	json_item = {}	json_item['Chinese'] = Chinese	json_item['English'] = English	print(json_item)		bug_type_translation.insert(json_item)	f_bug_type_translation.close()